    public static void main(String[] args) {

        JSONArray array = JSON.parseArray(getAreas());
        System.out.println(array);

        Map<String, List<String>> citys = new HashMap<>();
        for (int i = 0; i < array.size(); i++) {
            JSONObject json = array.getJSONObject(i);
            String province = json.getString("label");
            List<String> cs = new ArrayList<>();
            JSONArray cityArray = json.getJSONArray("children");
            for (int j = 0; j < cityArray.size(); j++) {
                String label = cityArray.getJSONObject(j).getString("label");
                cs.add(label);
            }
            citys.put(province, cs);
        }

        System.out.println(citys);

    }

